import os
import requests
from datetime import datetime
import pytz


class Tools:
    def __init__(self):
        self.citation = True

    def search_online(self, product_name: str) -> str:
        """
        Search for a product by its name using Google when the database does not contain any information about this product.
        """
        return requests.get(
            f"http://fastapi:8000/search_online_by_name/?product_name={product_name}"
        ).text
